mapper.insert:
INSERT into authorities
(
  username,
  authority
)
VALUES
(
  #{username},
  #{role}
)

mapper.update:
UPDATE users SET
  enabled = #{enabled}
WHERE username = #{username}
AND registration_token = #{registrationToken}

UPDATE users SET
  password = #{password},
  enabled = #{enabled},
  registration_token = #{registrationToken}
WHERE username = #{username}

UPDATE users SET
  enabled = #{enabled},
  password = #{password}
WHERE
  username = #{username}
AND
  registration_token = #{registrationToken}

mapper.delete:
DELETE FROM subscriptions
WHERE
  email=#{userId}
AND
  podcast_id=#{podcastId}
